import React, { Component } from 'react'
import './what.css'
import Header from '../../components/Header'
import Footer from '../../components/Footer'
import banner from '../../Images/banner.png'
import vector1 from '../../Images/vector1.png'
import vector2 from '../../Images/vector2.png'
import vector3 from '../../Images/vector3.png'
import vector4 from '../../Images/vector4.png'
import { HashLink as Link } from 'react-router-hash-link';
import $ from 'jquery'

class Whatwedo extends Component{
componentDidMount = () => {
    $(document).ready(function() {
        /*var $container = $('.grid').isotope({
          itemSelector : '.element-item',
          isFitWidth: true
        });
        $container.isotope({ filter: '*' });
        $('.filters').on( 'click', 'li', function() {
          var filterValue = $(this).attr('data-filter');
          $container.isotope({ filter: filterValue });
        });*/

        /* hash function*/

        // filter functions
        function getHashFilter() {
            var hash = location.hash;
            var matches = location.hash.match(/filter=([^&]+)/i);
            var hashFilter = matches && matches[1];
            return hashFilter && decodeURIComponent(hashFilter);
        }

        $(function() {

            var $grid = $('.grid');
            var $filterButtonGroup = $('.filters');
            $filterButtonGroup.on('click', 'li', function() {
                var filterAttr = $(this).attr('data-filter');

                location.hash = 'filter=' + encodeURIComponent(filterAttr);
            });

            var isIsotopeInit = false;

            function onHashchange() {
                var hashFilter = getHashFilter();
                if (!hashFilter && isIsotopeInit) {
                    return;
                }
                isIsotopeInit = true;
                $grid.isotope({
                    itemSelector: '.element-item',
                    filter: hashFilter
                });

                if (hashFilter) {
                    $filters.find('.is-checked').removeClass('is-checked');
                    $filters.find('[data-filter="' + hashFilter + '"]').addClass('is-checked');
                }
            }

            $(window).on('hashchange', onHashchange);
            onHashchange();
        });


    });
    $(function() {

        $(".videos").ycp({
            apikey: 'AIzaSyA8KtdyQt9vLbZChZUM3RNN1xuqTew5Uuw',
            autoplay: false,
            related: false
        });
        $(".dropdown-menu li a").click(function() {
            $(this).parents(".dropdown").find('.category-btn').html($(this).text() + ' <span class="caret"></span>');
            $(this).parents(".dropdown").find('.category-btn').val($(this).data('value'));
        });
    });

}
    render() {
        return(
            <div id="site-wrapper">
                <Header />
                    <div id="page-content">
                        <div className="">
                            <img src={banner} className="banner1" alt="" />
                        </div>
                        <div className="fashion-page">
                            <div className="fashion-content">
                                <div className="section1">
                                    <img src={vector1} className="align-middle vector1" alt="" />
                                </div>
                                <div className="fashion-text">
                                    <h2 className="f-head">Fashion Portfolio</h2>
                                    <p className="f-text">
                                    Creative. Strategic. Effective. An uncommon blend that will take your image to the next
                                    level. Our creative team knows how to focus on the details that build a brand, while
                                    understanding the strategy driving the business. From digital to print, we got it all.
                                    </p>
                                    <a class="btn btn-theme" href="/Gallery#filter=.Fashion">View our case studies</a>
                                   {/* <Link to="Gallery#filter=.Fashion"><button type="button" class="btn design-btn">OUR PORTFOLIO</button></Link> */}
                                </div>
                            </div>
                        </div>
                        <div className="Events-page">
                            <div className="events-content">
                            <div className="section2">
                                <img src={vector2} className="vector2" alt="" />
                            </div>
                            <div className="Event-text">
                            <h2 className="E-head" style={{fontFamily: "Poppins"}}>Events</h2>
                                    <p className="E-text">
                                    Creative. Strategic. Effective. An uncommon blend that will take your image to the next
                                    level. Our creative team knows how to focus on the details that build a brand, while
                                    understanding the strategy driving the business. From digital to print, we got it all.
                                    </p>
                                  <button type="button" class="btn design-btn" style={{float: "left"}}>OUR PORTFOLIO</button>
                                </div>
                            </div>
                        </div>
                        <div className="Wedding-page">
                            <div className="fashion-content">
                                <div className="section1">
                                    <img src={vector3} className="vector3" alt="" />
                                </div>
                                <div className="fashion-text">
                                    <h2 className="f-head" style={{fontFamily: "Poppins"}}>Wedding Shoots</h2>
                                    <p className="f-text">
                                    Creative. Strategic. Effective. An uncommon blend that will take your image to the next
                                    level. Our creative team knows how to focus on the details that build a brand, while
                                    understanding the strategy driving the business. From digital to print, we got it all.
                                    </p>
                                 <Link to="Gallery#portfolio3"><button type="button" class="btn design-btn">OUR PORTFOLIO</button></Link>
                                </div>
                            </div>
                        </div>

                        <div className="Product-page">
                        <div className="events-content">
                            <div className="section2">
                                <img src={vector4} className="vector2" alt="" />
                            </div>
                            <div className="Event-text">
                            <h2 className="E-head" style={{fontFamily: "Poppins"}}>Product Shoots</h2>
                                    <p className="E-text">
                                    Creative. Strategic. Effective. An uncommon blend that will take your image to the next
                                    level. Our creative team knows how to focus on the details that build a brand, while
                                    understanding the strategy driving the business. From digital to print, we got it all.
                                    </p>
                                    <button type="button" class="btn design-btn" style={{float: "left"}}>OUR PORTFOLIO</button>
                                </div>
                            </div>
                        </div>
                    </div>
                <Footer />
            </div>
        );
    }
}

export default Whatwedo
