import React, { Component } from 'react'
import $ from 'jquery'
import './works.css'

import image1 from '../../Images/image1.jpg'
import image2 from '../../Images/image2.jpg'
import image3 from '../../Images/image3.jpg'

import image5 from '../../Images/image5.jpg'
import image6 from '../../Images/image6.jpg'
import image8 from '../../Images/image8.jpg'
import image9 from '../../Images/image9.jpg'
import image10 from '../../Images/image10.jpg'
import image11 from '../../Images/image11.jpg'
import image12 from '../../Images/image12.jpg'
import image13 from '../../Images/image13.jpg'
import image14 from '../../Images/image14.jpg'

class Works extends Component{
    componentDidMount = () =>{
        $(document).ready(function(){
            $(".button").click(function(){
                var name = $(this).attr("data-filter");
                if(name === "all"){
                    $(".filter").show("2000");
                }
                else{
                    $(".filter").not("."+name).hide("2000");
                    $(".filter").filter("."+name).show("2000");
                }
            });
            $(".navigation a").click(function(){
                $(this).addClass("active").siblings().removeClass("active");
            })
        });
        $(document).ready(function() {
            /*var $container = $('.grid').isotope({
              itemSelector : '.element-item',
              isFitWidth: true
            });
            $container.isotope({ filter: '*' });
            $('.filters').on( 'click', 'li', function() {
              var filterValue = $(this).attr('data-filter');
              $container.isotope({ filter: filterValue });
            });*/

            /* hash function*/

            // filter functions
            function getHashFilter() {
                var hash = location.hash;
                var matches = location.hash.match(/filter=([^&]+)/i);
                var hashFilter = matches && matches[1];
                return hashFilter && decodeURIComponent(hashFilter);
            }

            $(function() {

                var $grid = $('.grid');
                var $filterButtonGroup = $('.filters');
                $filterButtonGroup.on('click', 'li', function() {
                    var filterAttr = $(this).attr('data-filter');

                    location.hash = 'filter=' + encodeURIComponent(filterAttr);
                });

                var isIsotopeInit = false;

                function onHashchange() {
                    var hashFilter = getHashFilter();
                    if (!hashFilter && isIsotopeInit) {
                        return;
                    }
                    isIsotopeInit = true;
                    $grid.isotope({
                        itemSelector: '.element-item',
                        filter: hashFilter
                    });

                    if (hashFilter) {
                        $filters.find('.is-checked').removeClass('is-checked');
                        $filters.find('[data-filter="' + hashFilter + '"]').addClass('is-checked');
                    }
                }

                $(window).on('hashchange', onHashchange);
                onHashchange();
            });


        });
        $(function() {

            $(".videos").ycp({
                apikey: 'AIzaSyA8KtdyQt9vLbZChZUM3RNN1xuqTew5Uuw',
                autoplay: false,
                related: false
            });
            $(".dropdown-menu li a").click(function() {
                $(this).parents(".dropdown").find('.category-btn').html($(this).text() + ' <span class="caret"></span>');
                $(this).parents(".dropdown").find('.category-btn').val($(this).data('value'));
            });
        });

    }


    render() {
        return(
            <div className="latest-work">
            <h1 className="work-text1">Latest <span className="work-text2">Works</span></h1>
            <div className="navigation">
                <a href="#" data-filter="all" class="button active">All</a>
                <a href="#" data-filter=".Fashion" class="button">Fashion Portfolio</a>
                <a href="#" data-filter=".Event" class="button">Events</a>
                <a href="#" data-filter=".Wedding" class="button">Wedding</a>
                <a href="#" data-filter=".Product" class="button">Product Shoots</a>
            </div>
             <div id="fashion-portfolio">
                <div className="filter Fashion">
                    <img src={image1}  />
                </div>
                <div className="filter Fashion">
                    <img src={image2} />
                </div>
                <div className="filter Fashion" >
                    <img src={image3} />
                </div>

                <div className="filter Fashion" >
                    <img src={image5} />
                </div>
                <div className="filter Fashion" >
                    <img src={image6} />
                </div>
                <div className="filter Wedding">
                    <img src={image8} />
                </div>
                <div className="filter Wedding">
                    <img src={image9} />
                </div>
                <div className="filter Product">
                    <img src={image10} />
                </div>
                <div className="filter Product">
                    <img src={image11} />
                </div>
                <div className="filter Event">
                    <img src={image12} />
                </div>
                <div className="filter Event">
                    <img src={image13} />
                </div>
                <div className="filter Event">
                    <img src={image14} />
                </div>


            </div>

    </div>
        );
    }
}

export default Works;
