import React, { Component } from 'react'
import $ from 'jquery'
import './works.css'
import lightbox from 'lightbox2'

import sample2 from '../../Video/sample2.mp4';
import sample3 from '../../Video/sample3.mp4';

import f1 from '../../Images/f1.jpg'
import f2 from '../../Images/f2.jpg'
import f3 from '../../Images/f3.jpg'
import f4 from '../../Images/f4.jpg'
import f5 from '../../Images/f5.jpg'
import f6 from '../../Images/f6.jpg'
import f7 from '../../Images/f7.jpg'
import f8 from '../../Images/f8.jpg'
import f9 from '../../Images/f9.png'
import f10 from '../../Images/f10.png'
import f11 from '../../Images/f11.png'
import f12 from '../../Images/f12.png'
import f13 from '../../Images/f13.png'

import w1 from '../../Images/w1.jpg'
import w2 from '../../Images/w2.jpg'
import w4 from '../../Images/w4.png'
import w5 from '../../Images/w5.png'
import w6 from '../../Images/w6.png'
import w7 from '../../Images/w7.png'
import w8 from '../../Images/w8.png'
import w9 from '../../Images/w9.png'
import e1 from '../../Images/e1.png'
import e2 from '../../Images/e2.png'
import e3 from '../../Images/e3.png'
import e4 from '../../Images/e4.png'
import e5 from '../../Images/e5.jpg'
import e6 from '../../Images/e6.jpg'
import e7 from '../../Images/e7.jpg'

import p1 from '../../Images/p1.png'
import p2 from '../../Images/p2.png'
import p3 from '../../Images/p3.png'
import p4 from '../../Images/p4.png'
import p5 from '../../Images/p5.png'
import p6 from '../../Images/p6.png'
import p7 from '../../Images/p7.png'
import p8 from '../../Images/p8.png'
import p9 from '../../Images/p9.png'
import p10 from '../../Images/p10.png'
import p11 from '../../Images/p11.png'
import p12 from '../../Images/p12.png'
import p13 from '../../Images/p13.png'




class Works extends Component{
    componentDidMount = () =>{
        $(document).ready(function(){
            // filter
            $('nav a').on('click', function(event){
                event.preventDefault();
                // current class
                $('nav li.current').removeClass('current');
                $(this).parent().addClass('current');

                // set new heading
                $('h1.heading').text($(this).text());

                // filter link text
                var category = $(this).text().toLowerCase().replace(' ', '-');

                // remove hidden class if "all" is selected
                if(category == 'all-projects'){
                    $('ul#gallery li:hidden').fadeIn('slow').removeClass('hidden');
                } else {
                    $('ul#gallery li').each(function(){
                       if(!$(this).hasClass(category)){
                           $(this).hide().addClass('hidden');
                       } else {
                           $(this).fadeIn('slow').removeClass('hidden');
                       }
                    });
                }
                return false;        
            });
            // lightbox
            $('ul#gallery a').on('click', function(event){
                event.preventDefault();
                var link = $(this).find('img').attr('src');
                $('.gallery img').attr('src', '');
                $('.gallery img').attr('src', link);
                $('.gallery').fadeIn('slow');
            });
            // close lightbox
            $('.gallery').on('click', function(event){
                event.preventDefault();
                $('.gallery').fadeOut('slow');
            });
        });
    }

    render() {
        return(
            <div className="latest-work">
            <h1 className="work-text1">Latest <span className="work-text2">Works</span></h1>

            <div class="container">
            <nav>
                <ul>
                    <li class="current"><a href="#">All projects</a></li>
                    <li><a href="#">Front-end</a></li>
                    <li><a href="#">Back-end</a></li>
                    <li><a href="#">Apps</a></li>
                    <li><a href="#">Design</a></li>              
                </ul>
            </nav>
            <div id="projects">
                <h1 class="heading">All Projects</h1>
                <ul id="gallery">
                    <li class="design"><a href="#"><img src={f1} /></a></li>
                    <li class="apps"><a href="#"><img src="https://source.unsplash.com/y1yQQmozTBw/700x700" /></a></li>
                    <li class="back-end"><a href="#"><img src="https://source.unsplash.com/b18TRXc8UPQ/700x700" /></a></li>
                    <li class="apps design"><a href="#"><img src="https://source.unsplash.com/klRB1BB9pV8/700x700" /></a></li>
                    <li class="front-end"><a href="#"><img src="https://source.unsplash.com/y1yQQmozTBw/700x700" /></a></li>
                    <li class="front-end design"><a href="#"><img src="https://source.unsplash.com/1vwwZ-BmmrE/700x700" /></a></li>
                    <li class="apps"><a href="#"><img src="https://source.unsplash.com/WLOCr03nGr0/700x700" /></a></li>
                    <li class="back-end"><a href="#"><img src="https://source.unsplash.com/iOykDIkZLQw/700x700" /></a></li>
                </ul>
                </div>
            </div>

            <div class="gallery">
                <a class="close" href="#">
                    <i>
                    <span class="bar"></span>
                    <span class="bar"></span>
                    </i>
                </a>
                <img src="" />
            </div>
        </div>

        );
    }
}

export default Works;
